import React, {Component} from 'react';
import './App.css';

function getRandom(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

function Box1(props){
  return (
    <input  class="box" value={props.val}  readOnly  />
  )
}


export default class App extends React.Component {
  //this.state = {value: ''};
  state = {
    value1 : getRandom(1,9),
    value2 : getRandom(1,9),
    result : ''
  }
  validateResult =(event) => {
    event.preventDefault()
    console.log(this.state.result + "," + this.state.value1 + " ," + this.state.value2)
  }
   render(){
  return (
  <div className="App">
    <header className="App-header">
    <p>
     VARDHAN G
     </p>
<form  onSubmit={this.validateResult}>
    <div >
       <Box1 val={this.state.value1}/>
       <Box1 val="+"/>
       <Box1 val={this.state.value2}/>
       <Box1 val="=" />
       <input class="resbox"
              value={this.state.result}
              onChange={event => this.setState({result: event.target.value})}
              required
        />
       <br/>
       <input type="submit" value="Submit" />
    </div>
</form>
     </header>
  </div>
   )
 }
}

