    return Math.floor(Math.random() * (max - min + 1)) + min;
}

function Box1(props){
  return (
    <input  class="box" value={props.val}  readOnly  />
  )
}

function Input(){
  return (
     <input maxlength="2" size="3"/>
  )
}

